(311, 311, NULL, 3, 'Fix for missing labels', '<br>Hi, <br><br>J3 Alpha 2 works flawless. I''ve fixed the missing labels in the spanish installer.<br><br>Thanks,<br>Anibal', 3, 0, 10, '2012-08-17 13:46:26', '2012-08-17 16:51:49', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/311.diff', NULL, NULL, NULL, NULL),
(312, 312, NULL, 3, 'Fix type attributes not required in html5. Thanks @nternetinspired', 'Issue reported by @nternetinspired in the template9 branch:<br>https://github.com/Joomla3-Admin-template/joomla-cms/issues/114<br><br>To enable the html5 mode in the template use:<br><br>    JFactory::getDocument()->setHtml5(true);<br><br>The type attributes will dissappear.', 3, 0, 1, '2012-08-18 00:56:57', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/312.diff', NULL, NULL, NULL, NULL),
