(394, 394, NULL, 3, 'Fix RTL select issue including installation and modals', 'Changed jquery''s chosen.css file to go to top instead of left with the .chzn-container-single-nosearch .chzn-search input selector. Since this css file is loaded via jquery.php, it is loaded after the rtl css files in some cases. Therefore we need to do the fix within this file itself.', 3, 0, 10, '2012-09-16 01:41:14', '2012-09-17 10:34:04', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/394.diff', NULL, NULL, NULL, NULL),
(395, 395, 28990, 3, '[#28990] Unpublish default menu item gives a PHP exception', 'Tracker issue link:<br>http://joomlacode.org/gf/project/joomla/tracker/?action=TrackerItemEdit&tracker_item_id=28990<br><br>Also changed the publish method to use joomla standards<br><br>', 3, 0, 10, '2012-09-16 02:11:12', '2012-09-19 10:27:25', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/395.diff', NULL, NULL, NULL, NULL),
(396, 396, NULL, 3, 'com_contact cat. list view', 'simple change to fix category item number badge alignment with category titles in com_contact category list views.<br>Problem seen here: http://s3.amazonaws.com/awesome_screenshot/755255?AWSAccessKeyId=0R7FMW7AXRVCYMAPTPR2&Expires=1347762135&Signature=So8FTnXEnUutiwYi%2BFRxz6titB0%3D', 3, 0, 10, '2012-09-16 02:15:13', '2012-09-16 03:24:48', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/396.diff', NULL, NULL, NULL, NULL),
