(414, 414, NULL, 3, 'Move installer and updater libraries to cms', 'CMS Tracker:<br>http://joomlacode.org/gf/project/joomla/tracker/?action=TrackerItemEdit&tracker_item_id=29235<br><br>Platform discussion;<br>https://groups.google.com/group/joomla-dev-platform/browse_thread/thread/1d6727a66eca9927.<br><br>Platform pull request will be created following successful CMS testing.<br><br>Testing of installer is successful.  Testing of updater currently fails.<br><br>The code change currently is a simple move of the installer and updater directories from libraries/joomla to libraries/cms.', 3, 0, 1, '2012-09-19 12:33:58', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/414.diff', NULL, NULL, NULL, NULL),
(415, 415, 28956, 3, '[#28956] No error message when saving a menu item with no title', 'Replaced visual clue-only (adding "invalid" class) which was missing<br>from template anyway by simple alter box when trying to save a menu item<br>with an empty title<br>Note: template should have:<br>.invalid {<br>	color: red;<br>	font-weight: bold;<br>}<br><br>input.invalid {<br>	border: 1px solid red;<br>}<br>for the bad field(s) to be displayed. Also, I removed the JS to highlight invalid field, for consistency', 3, 0, 1, '2012-09-19 16:47:42', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/415.diff', NULL, NULL, NULL, NULL),
