(438, 438, NULL, 3, 'Fix misplaced if/div/endif statements in blog_item', 'Fixes the problem with unmatched div if showing title when not showing author in a way that still allows author and unpublished badges to be shown if title is not shown.', 3, 0, 10, '2012-09-23 03:58:32', '2012-09-23 16:56:03', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/438.diff', NULL, NULL, NULL, NULL),
(439, 439, NULL, 3, 'removes <small>', '<small> is no longer a presentational element and so its use in this<br>context is incorrect. If the text needs to be small, that should be<br>achieved using css. Further details on correct usage of the small<br>element here: http://html5doctor.com/small-hr-element/', 3, 0, 1, '2012-09-24 19:38:11', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/439.diff', NULL, NULL, NULL, NULL),
(440, 440, NULL, 3, 'J layout interface change', 'SImplified JLayout interface, escape() not public anymore. JLayout is selfcontained.', 3, 0, 1, '2012-09-24 20:35:58', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/440.diff', NULL, NULL, NULL, NULL),
(441, 441, NULL, 3, 'RTL Testing Hack', 'Adds an RTL english dummy configuration[all translations fall through to the default english configuration] for easy testing by lame english speakers like me who don''t know any other languages.  Using this, a developer can set just his account into RTL mode to see what is actually be displayed without affecting display for anyone else.', 3, 0, 1, '2012-09-24 21:11:39', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/441.diff', NULL, NULL, NULL, NULL),
