(443, 443, 29301, 3, '[#29301] Protostar Site Title and Description Params', '- adding Site Title and Description param as fallbacks if there is no Site Logo present<br><br>- also fixing a couple of responsive display issues', 3, 0, 10, '2012-09-25 02:21:57', '2012-09-25 10:07:11', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/443.diff', NULL, NULL, NULL, NULL),
(444, 444, 29302, 3, '[#29302] Moving Accordion fix to bootstrap-extended', 'This patch removes the need for an entire extra bootstrap.css file being load<br>for one style fix, and moves that style fix into<br>/media/jui/less/bootstrap-extended.less (and bootstrap-extended.css) which is<br>compiled into the template.css of Isis and Protostar<br><br>That extra file is also removed from being called in<br>/libraries/cms/html/bootstrap.php<br><br>Lastly, this patch adds extra files that are needed if you''re not using LESS,<br>bootstrap-responsive.min.css and bootstrap-extended.css', 3, 0, 1, '2012-09-25 03:28:07', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/444.diff', NULL, NULL, NULL, NULL),
(445, 445, NULL, 3, 'Adding red colour to invalid fields', 'This pull requests adds red colour to invalid fields in the new backend template as it was in previous templates.<br><br>Here is the tracker item for that:<br>http://joomlacode.org/gf/project/joomla/tracker/?action=TrackerItemEdit&tracker_item_id=29305', 3, 0, 10, '2012-09-25 07:11:48', '2012-09-25 09:02:10', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/445.diff', NULL, NULL, NULL, NULL),
(446, 446, NULL, 3, 'Add Invalid indicator on invalid fields', 'Added styling for .invalid in bootstrap-extended.less file and recompiled the less files dependent on it. Minified files still need to be created.', 3, 0, 1, '2012-09-25 08:49:41', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/446.diff', NULL, NULL, NULL, NULL),
(447, 447, NULL, 3, 'Add jui css files to the generatecss.php file', 'If we have the generated css files in jui (which I think we should have) then we should add them to the list of files to be generated in the build.', 3, 0, 1, '2012-09-25 09:03:34', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/447.diff', NULL, NULL, NULL, NULL),
(448, 448, NULL, 3, 'getComponentsWithConfig for Custom Components', 'getComponentsWithConfig does not work for custom components at the moment because the column ''name'' is used in the database query instead of column ''element''.<br><br>Here is the tracker item for that:<br>http://joomlacode.org/gf/project/joomla/tracker/?action=TrackerItemEdit&tracker_item_id=29307', 3, 0, 10, '2012-09-25 09:42:04', '2012-10-11 05:51:57', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/448.diff', NULL, NULL, NULL, NULL),
(449, 449, NULL, 3, '[29269] fix mobile admin menu in Isis', '- this less/css update makes the admin menu much more usable on mobile devices<br><br>- tracker http://joomlacode.org/gf/project/joomla/tracker/?action=TrackerItemEdit&tracker_item_id=29269', 3, 0, 1, '2012-09-25 15:10:18', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/449.diff', NULL, NULL, NULL, NULL),
(450, 450, NULL, 3, 'Allow keyboard manipulation of button-like radio buttons in the install', 'This will work on Firefox but not on webkit browsers because they don''t respect the tabindex=0', 3, 0, 10, '2012-09-25 17:07:20', '2012-09-25 18:11:12', '0000-00-00 00:00:00', 'https://github.com/joomla/joomla-cms/pull/450.diff', NULL, NULL, NULL, NULL),
